import React, { Component } from "react";
import TopNewsByWing from "./TopNewsByWing";
import SearchBar from "../SearchBar";
import data from "../../data.json";
import ContainerByWing from "./ContainerByWing";
//import axios from "axios";

export default class Landing extends Component {
  state = {
    articles: [],
    query: "",
    wings: ["left", "left-center", "center", "center-right", "right"],
  };
  componentDidMount = () => {
    this.getData();
  };

  getData = () => {
    this.setState({ articles: data.articles });
  };

  setQuery = (query) => {
    this.setState({ query: query });
  };

  render() {
    return (
      <div>
        <SearchBar setQuery={this.setQuery} query={this.state.query} />
        <div className="article-container">
          {this.state.wings.map((wing) => (
            <ContainerByWing wing={wing} />
          ))}
        </div>
      </div>
    );
  }
}
